import React from 'react';
import style from './Input.module.css';

type InputProps = {
    className: string,
    value: string,
    name: string,
    placeholder: string,
    onChange?: () => void
}
export default function Input({...pros}: InputProps) {
    return (
    <label className={style.label_form}>
        <input
            className={style.input__text}
            value={formState.message}
            name="message"
            placeholder={'Ваше сообщение'}
            autoComplete="nope"
            type="text"
            onChange={inputMessage}
        />
        <span className={formState.errorMessage ? 'error-text' : 'none'}>
            {formState.errorMessage}
        </span>
    </label>) 
    
    // <input className={style.input} />
}

