import React, { useReducer, useState } from 'react';
import './styles/App.css';
import Header from 'components/Header/Header';
import { BrowserRouter } from 'react-router-dom';
import { reducer,formReducer, formCallbackReducer } from 'reducer/reducer';
import { Context } from 'Context/Context';
import AppRouter from './components/AppRouter';
import Footer from 'components/Footer/Footer';
import Modal from 'components/Modal/Modal';
import UpButton from 'components/UI/UpButton/UpButton';

function App() {
  const initialState = { isOpenModal: false, loading: true };
  const initialFormState = {
    name: '',
    phoneNumber: '',
    email: '',
    message: '',
    errorName: '',
    errorNumber: '',
    errorEmail: '',
    errorMessage: '',
  };
  const [state, dispatch] = useReducer(reducer, initialState);
  const [formState, formDispatch] = useReducer(formReducer, initialFormState);
  const initialFormCallbackState = {
    name: '',
    phoneNumber: '',
  };
  const [formCallbackState, formCallbackDispatch] = useReducer(
    formCallbackReducer,
    initialFormCallbackState
  );

  // const [formState, formDispatch] = useReducer(formReducer, initialFormState);
  const [isBurger, setIsBurger] = useState(false);
  const [catalogTitle, setCatalogTitle] = useState(String(localStorage.getItem('catalogTitle')));
  // const [loading, setLoading] = useState(true);
  return (
    <Context.Provider 
      value={{ 
        state, 
        dispatch,
        formState,
        formDispatch,
        formCallbackState,
        formCallbackDispatch,
        isBurger,
        setIsBurger,
        catalogTitle,
        setCatalogTitle,
        // forkliftState,
        // forkliftDispatch,
      }}>
      {/* <div className="App"> */}
        <BrowserRouter>
          <Header />
          <AppRouter />
          <Footer />
          <UpButton />
          <Modal />
        </BrowserRouter>
      {/* </div> */}
    </Context.Provider>
  );
}

export default App;
