import React, { useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import classes from './Header.module.css';

export default function Header() {
    const { state, dispatch } = useContext(Context);
    useEffect(() => {
        const timer = setTimeout(() => {
          if (dispatch) {
            dispatch({ type: 'resetModalCallback', payload: { isOpenModalCallback: true } });
          }
        }, 5000);
        return () => clearTimeout(timer);
      }, [dispatch]);
    return (
    <header className={classes.main__container}>
        <NavLink className="header__logo_wrapper" to="/">
            <img
                // src={logo}
                alt=""
                className="header__logo"
                onClick={() => {
                    window.scrollTo(0, 0);
                }}
            />
        </NavLink>
        <nav>
            <NavLink className="header__navbar_link" to="/">
                Главная
            </NavLink>
            <NavLink className="header__navbar_link" to="/catalog">
                Каталог
            </NavLink>
            <NavLink className="header__navbar_link" to="/about">
                О компании
            </NavLink>
            <NavLink className="header__navbar_link" to="/contacts">
                Контакты
            </NavLink>
        </nav>
    </header>
    );
}
