import React, { useContext, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import classes from './Header.module.css';
import { Context } from 'Context/Context';
import logo from '../../assets/logo.png';

export default function Header() {
    const { dispatch } = useContext(Context);
    useEffect(() => {
        const timer = setTimeout(() => {
        if (dispatch) {
            dispatch({ type: 'resetModal', payload: { isOpenModal: true } });
        }
        }, 5000);
        return () => clearTimeout(timer);
    }, [dispatch]);
    return (
    <header className={classes.header__container}>
        <NavLink className="header__logo_wrapper" to="/">
            <img
                src={logo}
                alt=""
                className={classes.header__logo}
                onClick={() => {
                    window.scrollTo(0, 0);
                }}
            />
        </NavLink >
        <nav className={classes.header__navbar}>
            <NavLink className="header__navbar_link" to="/">
                Главная
            </NavLink>
            <NavLink className="header__navbar_link" to="/catalog">
                Каталог
            </NavLink>
            <NavLink className="header__navbar_link" to="/about">
                О компании
            </NavLink>
            <NavLink className="header__navbar_link" to="/contacts">
                Контакты
            </NavLink>
        </nav>
        
    </header>
    );
}
