import React, { useContext, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import classes from './Header.module.css';
import { Context } from 'Context/Context';

export default function Header() {
    const { state, dispatch } = useContext(Context);

    useEffect(() => {
        dispatch({type: 'resetLoading', payload:{loading: false}})
        console.log('catalog: ', state.loading);
    }
    );
    console.log(state.loading);
    return (
      <header className={classes.main__container}>
            <NavLink className="header__logo_wrapper" to="/">
                <img
                    // src={logo}
                    alt=""
                    className="header__logo"
                    onClick={() => {
                        window.scrollTo(0, 0);
                    }}
                />
            </NavLink>
            <nav>
                <NavLink className="header__navbar_link" to="/">
                    Главная
                </NavLink>
                <NavLink className="header__navbar_link" to="/catalog">
                    Каталог
                </NavLink>
                <NavLink className="header__navbar_link" to="/about">
                    О компании
                </NavLink>
                <NavLink className="header__navbar_link" to="/contacts">
                    Контакты
                </NavLink>
            </nav>
      </header>
    );
  }
