          <Header />
          <AppRouter />
          <Footer />
          <UpButton />
          <Modal />
          <ModalCallback/>
        </BrowserRouter>
        {Parser().parse(rawHTML)}
      {/* </body> */}
    </Context.Provider>
  );
}

export default App;
