// eslint-disable-next-line @typescript-eslint/ban-ts-comment
// @ts-nocheck
/**
 * Helper method to remove keys from the object
 *
 * @param {Object} obj Object to remove keys from
 * @param {string[]} arr List of keys to remove
 * @returns {Object} Object with omitted keys
 */
export const omit = (obj, arr) => {
  const res = {};
  for (const n in obj) {
    if (arr.indexOf(n) === -1) res[n] = obj[n];
  }
  return res;
};
import React, { useContext } from 'react';
import { ApiLoader } from './util/create-api-loader';
import { AnyObject } from './util/typing';

export const YMapsApiLoaderContext = React.createContext<ApiLoader | null>(
  null
);

export const withYMapsContext = <TProps extends AnyObject>(
  Component: React.FC<React.PropsWithChildren<TProps>>
): React.FC<React.PropsWithChildren<TProps>> => {
  const WithYMapsContext: React.FC<React.PropsWithChildren<TProps>> = (
    props
  ) => (
    <YMapsApiLoaderContext.Consumer>
      {(apiLoader) => {
        if (apiLoader === null) {
          const message =
            "Couldn't find Yandex.Maps API in the context. " +
            `Make sure that <${Component.displayName} /> is inside <YMaps /> provider`;

          throw new Error(message);
        }

        // eslint-disable-next-line @typescript-eslint/ban-ts-comment
        // @ts-ignore
        return <Component apiLoader={apiLoader} {...props} />;
      }}
    </YMapsApiLoaderContext.Consumer>
  );

  return WithYMapsContext;
};

export const useYMapsApiLoader = () => {
  const apiLoader = useContext(YMapsApiLoaderContext);
  if (apiLoader === null) {
    const message =
      "Couldn't find Yandex.Maps API in the context. " +
      `Make sure that hook useYMaps is inside <YMaps /> provider`;

    throw new Error(message);
  }

  return apiLoader;
};

export const ParentContext = React.createContext(null);

export const withParentContext = <TProps extends AnyObject>(
  Component: React.FC<React.PropsWithChildren<TProps>>
): React.FC<React.PropsWithChildren<TProps>> => {
  const WithParentContext: React.FC<React.PropsWithChildren<TProps>> = (
    props
  ) => (
    <ParentContext.Consumer>
      {(parent) => {
        // eslint-disable-next-line @typescript-eslint/ban-ts-comment
        // @ts-ignore
        return <Component parent={parent} {...props} />;
      }}
    </ParentContext.Consumer>
  );

  return WithParentContext;
};
import { useEffect, useRef, useState } from 'react';
import { useYMapsApiLoader } from '../Context';
import { YMapsApi } from '../util/typing';

/**
 * Return loaded ymaps instance
 * @param modules
 * @param onError
 */
export const useYMaps = (modules: string[] = []): YMapsApi | null => {
  const [loaded, setLoaded] = useState(false);
  const modulesRef = useRef(modules);
  const apiLoader = useYMapsApiLoader();
  const api = apiLoader.getApi();

  useEffect(() => {
    apiLoader
      .load()
      .then(() => Promise.all(modulesRef.current.map(apiLoader.loadModule)))
      .then(() => setLoaded(true));
  }, []);

  return loaded && api ? api : null;
};
import React, { useEffect } from 'react';
import { omit } from '../util/omit';
import { AnyObject, YMapsApi, YMapsModules } from '../util/typing';
import { ApiLoader } from '../util/create-api-loader';
import { useYMaps } from '../hooks/useYMaps';

export interface WithYMapsProps {
  modules?: YMapsModules;
  width?: string | number;
  height?: string | number;
  onLoad?: (api: YMapsApi) => void;
}

const defaultFunction = () => void 0;
const omitProps = ['onLoad', 'onError', 'modules', 'apiLoader'];

export default function withYMaps<TProps extends AnyObject>(
  Component: React.FC<TProps> | React.Component<TProps>,
  waitForApi = false,
  hocModules: YMapsModules = []
): React.FC<TProps> {
  const WithYMaps: React.FC<WithYMapsProps & { apiLoader: ApiLoader }> = (
    props
  ) => {
    const { width, height, modules = [], onLoad = defaultFunction } = props;
    const ymaps = useYMaps(hocModules.concat(modules));
    const shouldRender = !waitForApi || !!ymaps;
    const newProps = omit(props, omitProps);

    useEffect(() => (ymaps ? onLoad(ymaps) : void 0), [ymaps]);

    if (!shouldRender) {
      return <div style={{ width, height }} />;
    }

    // eslint-disable-next-line @typescript-eslint/ban-ts-comment
    // @ts-ignore
    return <Component ymaps={ymaps} {...newProps} />;
  };

  return WithYMaps as unknown as React.FC<React.PropsWithChildren<TProps>>;
}
/**
 * Set value in object by path
 *
 * @param {Object} object Object to set value to
 * @param {string | string[]} path Path to value
 * @param {any} value Value
 * @param {boolean} [ifNotExists] Will skip setting value if value exists
 */
export const set = <TValue>(
  object: Record<string, unknown>,
  path: string | string[],
  value: TValue,
  ifNotExists = false
) => {
  const setPath = typeof path === 'string' ? path.split('.') : path.slice();

  let key: string;
  let ref = object;

  while (setPath.length > 1) {
    // eslint-disable-next-line @typescript-eslint/no-non-null-assertion
    key = setPath.shift()!;
    if (!ref[key]) ref[key] = {};
    ref = ref[key] as Record<string, unknown>;
  }

  // eslint-disable-next-line @typescript-eslint/no-non-null-assertion
  const lastPathKey = setPath[0]!;

  ref[lastPathKey] = ifNotExists === true ? ref[lastPathKey] || value : value;
  return object;
};
export const isBrowser = typeof window !== 'undefined';
import { set } from './set';
import { AnyObject, YMapsApi, YMapsModules } from './typing';
import { isDevEnv } from './is-dev-env';
import { isBrowser } from './is-browser';

interface YMapsQuery {
  lang?: 'tr_TR' | 'en_US' | 'en_RU' | 'ru_RU' | 'ru_UA' | 'uk_UA';
  apikey?: string;
  coordorder?: 'latlong' | 'longlat';
  load?: string;
  mode?: 'release' | 'debug';
  csp?: boolean;
  ns?: string;
}

interface CreateYMapsLoaderOptions {
  version?: string;
  enterprise?: boolean;
  query?: YMapsQuery;
  preload?: boolean;
}

export type ApiLoader = ReturnType<typeof createApiLoader>;

const YMAPS_ONLOAD = '__yandex-maps-api-onload__';
const YMAPS_ONERROR = '__yandex-maps-api-onerror__';

const YMAPS_DEFAULT_QUERY: YMapsQuery = {
  lang: 'ru_RU',
  load: '',
  ns: '',
  mode: isDevEnv ? 'debug' : 'release',
};

const getBaseUrl = (isEnterprise?: boolean) =>
  `https://${isEnterprise ? 'enterprise.' : ''}api-maps.yandex.ru`;

const fetchScript = (url: string) => {
  return new Promise((resolve, reject) => {
    const script = document.createElement('script');

    script.type = 'text/javascript';
    script.onload = resolve;
    script.onerror = reject;
    script.src = url;
    script.async = true;

    document.head.appendChild(script);
  });
};

export const createApiLoader = (options: CreateYMapsLoaderOptions) => {
  const { query = YMAPS_DEFAULT_QUERY } = options;
  const hash = Date.now().toString(32);
  const namespace = query.ns || '';
  const onload = YMAPS_ONLOAD + '$$' + hash;
  const onerror = YMAPS_ONERROR + '$$' + hash;
  const windowObj: AnyObject = isBrowser ? window : {};

  const PROMISES: Record<string, Promise<YMapsApi> | undefined> = {};
  let api: YMapsApi;

  const getApi = (): YMapsApi =>
    typeof isBrowser && namespace ? windowObj[namespace] : api;

  const loadModule = (moduleName: string): Promise<YMapsApi> => {
    return new Promise((res, rej) => {
      api.modules.require(moduleName).done((modules: YMapsModules[]) => {
        modules.forEach((module) => {
          set(api, moduleName, module, true);
        });

        res(api);
      }, rej);
    });
  };

  const clearWindow = () => {
    delete windowObj[onload];
    delete windowObj[onerror];
  };

  const load = (): Promise<YMapsApi> => {
    if (getApi()) {
      return Promise.resolve(api);
    }

    if (PROMISES[namespace]) {
      return PROMISES[namespace] as Promise<YMapsApi>;
    }

    const ymapsQuery: Record<string, string | boolean> = {
      onload,
      onerror,
      ...YMAPS_DEFAULT_QUERY,
      ...query,
    };

    const queryString = Object.keys(ymapsQuery)
      .map((key) => `${key}=${ymapsQuery[key]}`)
      .join('&');

    const baseUrl = getBaseUrl(options.enterprise);

    const url = [baseUrl, options.version, '?' + queryString].join('/');

    PROMISES[namespace] = new Promise<YMapsApi>((resolve, reject) => {
      windowObj[onload] = (ym: YMapsApi) => {
        clearWindow();

        void ym.ready(() => {
          api = ym;

          resolve(ym);
        });
      };

      windowObj[onerror] = (err: Error) => {
        clearWindow();
        reject(err);
      };

      fetchScript(url).catch(windowObj[onerror]);
    });

    return PROMISES[namespace] as Promise<YMapsApi>;
  };

  return { load, getApi, loadModule };
};
import React, { useEffect, useRef } from 'react';
import { YMapsApiLoaderContext } from './Context';
import { createApiLoader } from './util/create-api-loader';

interface YMapProvider {
  version?: string;
  enterprise?: boolean;
  /**
   *  Yandex.Maps API avaliable query params
   *  https://tech.yandex.com/maps/doc/jsapi/2.1/dg/concepts/load-docpage/
   *  Some query params will be omitted in any case because they are used
   *  by the library: onload, onerror
   */
  query?: {
    lang?: 'tr_TR' | 'en_US' | 'en_RU' | 'ru_RU' | 'ru_UA' | 'uk_UA';
    apikey?: string;
    suggest_apikey?: string;
    coordorder?: 'latlong' | 'longlat';
    load?: string;
    mode?: 'release' | 'debug';
    csp?: boolean;
    ns?: string;
  };
  /**
   *  Allows provider to preload Yandex.Maps API even if
   *  there are no map components on the page
   */
  preload?: boolean;
}

export const Provider: React.FC<React.PropsWithChildren<YMapProvider>> = (
  props
) => {
  const {
    version = '2.1',
    enterprise = false,
    query = { lang: 'ru_RU', load: '', ns: '' },
    preload = false,
    children,
  } = props;

  const ymapsRef = useRef(
    createApiLoader({ version, enterprise, query, preload })
  );

  useEffect(() => {
    if (preload) {
      ymapsRef.current.load();
    }
  }, [ymapsRef.current]);

  return (
    <YMapsApiLoaderContext.Provider value={ymapsRef.current}>
      {children}
    </YMapsApiLoaderContext.Provider>
  );
};
// eslint-disable-next-line @typescript-eslint/ban-ts-comment
