import React, { useState, useEffect } from 'react';
import classes from './DinamicCounter.module.css';

type Props = {
  number: number,
}

export default function DinamicCounter({number}: Props) {
    const [counter, setCounter] = useState(0);
    useEffect(() => {
        while(counter < number) {
            setInterval(() => setCounter(counter + 1), 500);
        }
    });
    const updateCounter = () => {
        while(counter < number) {
            setInterval(() => setCounter(counter + 1), 500);
        }
    }
    return (
      <span className={classes.counterWindow} onClick={updateCounter}>{counter}</span>
    );
  }
