import React, { useEffect, useState } from 'react';
import classes from './CatalogCardElem.module.css';
import { useNavigate, useParams } from 'react-router-dom';
// import ProductPage from '../../Pages/ProductPage/ProductPage';
import { Page } from 'types/types';
import {data} from '../../data/data';

type CatalogCardElem = {
    onClick?: () => void
}



export default function CatalogCardElem() {
    const navigate = useNavigate();
    const {id} = useParams();
    const param = id;
    const dataN: Page[] = (data.filter((elem) => elem.nameLink == param)[0].pages);
    return (
        <main className={classes.catalog_forklift__container}>
            <h3 className={classes.catalog_forklift__container_title}>{(data.filter((elem) => elem.nameLink == param)[0].title)}</h3>
            <div className={classes.catalog_forklift_cards__container}>
                {dataN.map((el)=> {
                    return (
                        <div key = {el.titleProduct} className={classes.catalog_forklift_card} onClick={() => navigate(`/catalog/${(data.filter((elem) => elem.nameLink == param)[0].nameLink)}/${el.nameLinkProduct}`)}>
                            <span className={classes.catalog_forklift_card_title}><strong>{el.titleProduct}</strong></span>
                            <img src={el.imgProduct} alt='Погрузчик' className={classes.catalog_forklift_card_img}/>
                            <div className={classes.catalog_forklift_card_text_block}>
                            {el.description?.manufacturer ? <span className={classes.catalog_forklift_card_text}>Производитель: {el.description.manufacturer}</span> : null} 
                            {el.description?.loadСapacity ? <span className={classes.catalog_forklift_card_text}>Грузоподъемность: {el.description.loadСapacity}</span> : null}
                            </div>
                        </div>
                    )
                })}
            </div>
        </main>
  );
}
